
SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

--
-- Base de données : `BDDA-project`
--

-- --------------------------------------------------------

--
-- Structure de la table `Game`
--

CREATE TABLE `Game` (
  `Id_game` int(11) NOT NULL,
  `Name_game` text DEFAULT NULL,
  `Description` text DEFAULT NULL,
  `minplayer` int(11) DEFAULT NULL,
  `maxplayer` int(11) DEFAULT NULL,
  `playingtime` int(11) DEFAULT NULL,
  `minplaytime` int(11) DEFAULT NULL,
  `maxplaytime` int(11) DEFAULT NULL,
  `Average_Note` decimal(15,2) DEFAULT NULL,
  `Year_published` int(11) DEFAULT NULL,
  `rank_game` int(11) DEFAULT NULL,
  `id_publisher` int(11) NOT NULL,
  `Id_category` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- --------------------------------------------------------

--
-- Structure de la table `Boardgamepublisher_`
--

CREATE TABLE `Boardgamepublisher_` (
  `id_publisher` int(11) NOT NULL,
  `boardgamefamily_` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;



-- --------------------------------------------------------
--
-- Structure de la table `Comment`
--

CREATE TABLE `Comment` (
  `Id_game` int(11) NOT NULL,
  `id_user` int(11) NOT NULL,
  `note` int(11) DEFAULT NULL,
  `avis` varchar(50) DEFAULT NULL,
  `date_note` date DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- --------------------------------------------------------

--
-- Structure de la table `Users`
--

CREATE TABLE `Users` (
  `id_user` int(11) NOT NULL,
  `Last_Name` varchar(50) DEFAULT NULL,
  `First_Name` varchar(50) DEFAULT NULL,
  `email` varchar(50) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- --------------------------------------------------------

--
-- Structure de la table `Favoris`
--

CREATE TABLE `Favoris` (
  `Id_game` int(11) NOT NULL,
  `id_user` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- --------------------------------------------------------

--
-- Structure de la table `Category`
--

CREATE TABLE `Category` (
  `Id_category` int(11) NOT NULL,
  `Name_category` varchar(50) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


--
-- Déclencheurs `Comment`
--
DELIMITER $$
CREATE TRIGGER `trg_update_average_note` AFTER INSERT ON `Comment` FOR EACH ROW BEGIN
  UPDATE Game
  SET Average_Note = (
    SELECT ROUND(AVG(note), 2)
    FROM Comment
    WHERE Id_game = NEW.Id_game
  )
  WHERE Id_game = NEW.Id_game;
END
$$
DELIMITER ;





DELIMITER $$
CREATE TRIGGER `trg_validate_rating_range` BEFORE INSERT ON `Comment` FOR EACH ROW BEGIN
  IF NEW.note < 1 OR NEW.note > 10 THEN
    SIGNAL SQLSTATE '45000'
    SET MESSAGE_TEXT = 'Rating (note) must be between 1 and 10.';
  END IF;
END
$$
DELIMITER ;



--
-- Déclencheurs `Favoris`
--
DELIMITER $$
CREATE TRIGGER `trg_prevent_duplicate_favorite` BEFORE INSERT ON `Favoris` FOR EACH ROW BEGIN
  IF EXISTS (
    SELECT 1 FROM Favoris
    WHERE Id_game = NEW.Id_game AND id_user = NEW.id_user
  ) THEN
    SIGNAL SQLSTATE '45000'
    SET MESSAGE_TEXT = 'This favorite already exists.';
  END IF;
END
$$
DELIMITER ;




DELIMITER $$
CREATE DEFINER=`root`@`localhost` FUNCTION `GetFavoriteCount`(gameId INT) RETURNS int(11)
    DETERMINISTIC
BEGIN
  DECLARE favCount INT;
  SELECT COUNT(*) INTO favCount
  FROM Favoris
  WHERE Id_game = gameId;
  RETURN favCount;
END$$
DELIMITER ;




DELIMITER $$
CREATE DEFINER=`root`@`localhost` FUNCTION `GetUserCommentCount`(userId INT) RETURNS int(11)
    DETERMINISTIC
BEGIN
  DECLARE commentCount INT;
  SELECT COUNT(*) INTO commentCount
  FROM Comment
  WHERE id_user = userId;
  RETURN commentCount;
END$$
DELIMITER ;



DELIMITER $$
CREATE DEFINER=`root`@`localhost` FUNCTION `IsRecentGame`(gameId INT) RETURNS tinyint(1)
    DETERMINISTIC
BEGIN
  DECLARE isRecent BOOLEAN;
  DECLARE pubYear INT;
  SELECT Year_published INTO pubYear FROM Game WHERE Id_game = gameId;
  SET isRecent = (pubYear >= YEAR(CURDATE()) - 2); 
  RETURN isRecent;
END$$
DELIMITER ;




DELIMITER $$
CREATE DEFINER=`root`@`localhost` PROCEDURE `AddComment`(
    IN p_Id_game INT,
    IN p_id_user INT,
    IN p_note INT,
    IN p_avis VARCHAR(50),
    IN p_date_note DATE
)
BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
    END;

    START TRANSACTION;

        Id_game, id_user, note, avis, date_note
    ) VALUES (
        p_Id_game, p_id_user, p_note, p_avis, p_date_note
    );

    COMMIT;
END$$
DELIMITER ;




DELIMITER $$
CREATE DEFINER=`root`@`localhost` PROCEDURE `AddGame`(
    IN p_Name_game TEXT,
    IN p_Id_category INT,
    IN p_id_publisher INT,
    IN p_minplaytime INT,
    IN p_maxplaytime INT,
    IN p_Year_published INT,
    IN p_minplayer INT,
    IN p_maxplayer INT
)
BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
    END;

    START TRANSACTION;

        Name_game, Id_category, id_publisher,
        minplaytime, maxplaytime, Year_published,
        minplayer, maxplayer
    ) VALUES (
        p_Name_game, p_Id_category, p_id_publisher,
        p_minplaytime, p_maxplaytime, p_Year_published,
        p_minplayer, p_maxplayer
    );

    COMMIT;
END$$
DELIMITER ;




DELIMITER $$
CREATE DEFINER=`root`@`localhost` PROCEDURE `RegisterUser`(
    IN p_Last_Name VARCHAR(50),
    IN p_First_Name VARCHAR(50),
    IN p_email VARCHAR(50)
)
BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
    END;

    START TRANSACTION;

    VALUES (p_Last_Name, p_First_Name, p_email);

    COMMIT;
END$$
DELIMITER ;


CREATE OR REPLACE VIEW TrendingGames AS
SELECT 
    g.Name_game,
    ROUND(AVG(c.note), 2) AS AverageRating,
    COUNT(c.id_user) AS NumberOfRatings,
    g.Id_game
FROM Game g
JOIN Comment c ON g.Id_game = c.Id_game
GROUP BY g.Id_game, g.Name_game
ORDER BY AverageRating DESC, NumberOfRatings DESC
LIMIT 10;




CREATE OR REPLACE VIEW AdminStats AS
SELECT 
    (SELECT COUNT(*) FROM Game) AS TotalGames,
    (SELECT COUNT(*) FROM Users) AS TotalUsers,
    (SELECT COUNT(*) FROM Comment) AS TotalComments,
    (SELECT COUNT(*) FROM Favoris) AS TotalFavorites;




CREATE OR REPLACE VIEW GameFavoritesRanking AS
SELECT 
    g.Id_game,
    g.Name_game,
    COUNT(f.id_user) AS FavoriteCount,
    RANK() OVER (ORDER BY COUNT(f.id_user) DESC) AS RankPosition
FROM Game g
LEFT JOIN Favoris f ON g.Id_game = f.Id_game
GROUP BY g.Id_game, g.Name_game;



-- Créer l'utilisateur admin avec un mot de passe
CREATE OR REPLACE USER 'admin'@'localhost' IDENTIFIED BY 'admin_password';

-- Créer l'utilisateur user avec un mot de passe
CREATE OR REPLACE USER 'user'@'localhost' IDENTIFIED BY 'user_password';

-- Create the admin and user role
CREATE OR REPLACE ROLE 'admin_role';
CREATE OR REPLACE ROLE 'user_role';

-- Vue AdminStats
GRANT SELECT ON AdminStats TO 'admin'@'localhost';

-- Accès complet aux tables
GRANT ALL PRIVILEGES ON Boardgamepublisher_ TO 'admin_role';
GRANT ALL PRIVILEGES ON Category TO 'admin_role';
GRANT ALL PRIVILEGES ON Comment TO 'admin_role';
GRANT ALL PRIVILEGES ON Favoris TO 'admin_role';
GRANT ALL PRIVILEGES ON Game TO 'admin_role';
GRANT ALL PRIVILEGES ON Users TO 'admin_role';

-- Accès aux vues
GRANT SELECT ON TrendingGames TO 'admin_role';
GRANT SELECT ON GameFavoritesRanking TO 'admin_role';

-- Fonctions
GRANT EXECUTE ON FUNCTION GetFavoriteCount TO 'admin_role';
GRANT EXECUTE ON FUNCTION IsRecentGame TO 'admin_role';
GRANT EXECUTE ON FUNCTION GetUserCommentCount TO 'admin_role';

-- Procédures stockées
GRANT EXECUTE ON PROCEDURE AddGame TO 'admin_role';
GRANT EXECUTE ON PROCEDURE AddComment TO 'admin_role';
GRANT EXECUTE ON PROCEDURE RegisterUser TO 'admin_role';

-- Accès aux tables
GRANT SELECT ON Boardgamepublisher_ TO 'user_role';
GRANT SELECT ON Category TO 'user_role';

-- Accès aux commentaires et favoris
GRANT SELECT, INSERT ON Comment TO 'user_role';
GRANT SELECT, INSERT, DELETE, UPDATE ON Favoris TO 'user_role';

-- Vues accessibles
GRANT SELECT ON TrendingGames TO 'user_role';
GRANT SELECT ON GameFavoritesRanking TO 'user_role';

-- Fonctions nécessaires selon les accès
GRANT EXECUTE ON FUNCTION GetFavoriteCount TO 'user_role';
GRANT EXECUTE ON FUNCTION IsRecentGame TO 'user_role';
GRANT EXECUTE ON FUNCTION GetUserCommentCount TO 'user_role';

-- Procédures autorisées selon les accès
GRANT EXECUTE ON PROCEDURE AddComment TO 'user_role';

-- Admin gets the admin role and User gets the user role
GRANT 'admin_role' TO 'admin'@'localhost';
GRANT 'user_role' TO 'user'@'localhost';


-- Activer le rôle pour un utilisateur (par exemple, admin)
SET ROLE 'admin_role';

-- Activer le rôle pour un autre utilisateur (par exemple, user)
SET ROLE 'user_role';

